import { createSlice } from "@reduxjs/toolkit";

const testimonialsSlice = createSlice({
  name: "testimonials",
  initialState: [],
  reducers: {
    addTestimonial: (state, action) => {
      state.push(action.payload);
    },
    updateTestimonial: (state, action) => {
      const { index, firstName, email, message, rating } = action.payload;
      state[index] = { firstName, email, message, rating };
    },
  },
});

export const { addTestimonial, updateTestimonial } = testimonialsSlice.actions;
export default testimonialsSlice.reducer;
