import React, { useState } from "react";
import Head from "next/head";
import styles from "./App.css"; // Assuming you have a CSS module for styling

const Signuppage = () => {
  const [isLogin, setIsLogin] = useState(true);

  const toggleAuthMode = () => {
    setIsLogin(!isLogin);
  };

  return (
    <>
      <Head>
        <title>{isLogin ? "Login" : "Signup"}</title>
      </Head>
      <section id="auth" className={styles.authSection}>
        <div className={styles.authContainer}>
          <h2>{isLogin ? "Login" : "Signup"}</h2>
          <form className={styles.authForm}>
            {!isLogin && (
              <div className={styles.formGroup}>
                <label htmlFor="name">Name</label>
                <input type="text" id="name" name="name" required />
              </div>
            )}
            <div className={styles.formGroup}>
              <label htmlFor="email">Email</label>
              <input type="email" id="email" name="email" required />
            </div>
            <div className={styles.formGroup}>
              <label htmlFor="password">Password</label>
              <input type="password" id="password" name="password" required />
            </div>
            <button type="submit" className={styles.authButton}>
              {isLogin ? "Login" : "Signup"}
            </button>
          </form>
          <button onClick={toggleAuthMode} className={styles.toggleButton}>
            {isLogin
              ? "Don't have an account? Signup"
              : "Already have an account? Login"}
          </button>
        </div>
      </section>
    </>
  );
};

export default Signuppage;
